CREATE TABLE roles (id BIGINT AUTO_INCREMENT NOT NULL, `label` VARCHAR(20) NOT NULL, description VARCHAR(100) DEFAULT NULL, name VARCHAR(20) NOT NULL, acl LONGTEXT NOT NULL, locked TINYINT(1) DEFAULT '0', author BIGINT DEFAULT NULL, status TINYINT(1) NOT NULL, created_at DATETIME NOT NULL, modified_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE users (id BIGINT AUTO_INCREMENT NOT NULL, mail VARCHAR(100) NOT NULL, login VARCHAR(50) NOT NULL, password VARCHAR(100) NOT NULL, name VARCHAR(100) NOT NULL, super_admin TINYINT(1) DEFAULT '0', roles LONGTEXT DEFAULT NULL, locked TINYINT(1) DEFAULT '0', author BIGINT DEFAULT NULL, status TINYINT(1) NOT NULL, created_at DATETIME NOT NULL, modified_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
